mapper.select:

select board_no, title, content, writer, write_date
from (
       select /*+ INDEX_DESC(tbl_board SYS_C0031805) */
              rownum rn, board_no, title, content, writer, write_date
       from tbl_board
       where rownum <= #{pageNum} * #{amount}
     )
where rn > (#{pageNum} - 1) * #{amount}

